  max-width: 1440px;
  height: 1px;
  background: var(--primary);
}
.copyright {
  padding: 1em;
  font-size: 22px;
  color: var(--primary);
}
@media (max-width: 320px) {
  .project-pic {
    width: 100%;
    height: 220px;
  }
}
@media (min-width: 360px) {
  .cta a {
    width: 100%;
  }
  .social-section {
    width: 80%;
  }
  .benifits {
    font-size: 1.5rem;
  }
  span {
    font-size: 1.2rem;
  }
  .designation {
    font-size: 1.2rem;
  }
}
@media (min-width: 550px) {
  .hero-section-content {
    max-width: 550px;
  }
  .project-card {
    width: 90%;
  }
  .benifits {
    font-size: 2.5rem;
  }
  span {
    font-size: 1.9rem;
  }
  .designation {
    font-size: 1.5rem;
  }
  .about .wraper-skills img {
    width: 90%;
  }
}
@media (min-width: 660px) {
  .header {
    display: flex;
    flex-direction: column;
    box-sizing: border-box;
    max-width: 1440px;
  }
  .nav {
    padding: 2em 3em;
  }
  .menu-btn {
    display: none;
  }
  .mobile-nav-bar {
    display: none;
  }
  .desktop-nav-bar {
    display: flex;
  }
  .main {
    padding: 5em 3em;
  }
  .hero-section {
    margin: 6em auto;
    flex-direction: row;
    justify-content: space-between;
  }
  .benifits {
    font-size: 2rem;
    text-align: left;
    margin: 0px;
  }
  span {
    font-size: 1.8rem;
  }
  .designation {
    text-align: left;
    font-size: 1.5rem;
  }
  .cta {
    flex-direction: row;
    width: 90%;
  }
  .social-section {
    width: fit-content;
    flex-direction: column;
  }
  .project-card {
    width: 80%;
  }
  .wraper-skills img {
    width: 20%;
  }
  .about .wraper-skills img {
    width: 70%;
    margin-right: 50px;
  }
  .wraper-skills p {
    font-size: 1.1rem;
    text-align: justify;
    color: var(--primary);
  }
  form {
    width: 90%;
  }
}
@media (min-width: 930px) {
  .desktop-nav-bar {
    width: 47%;
  }
  .project-list {
    width: 100%;
    display: flex;
    flex-direction: row;
    justify-content: space-around;
  }
  .project-pic {
    width: 100%;
    height: 220px;
  }
  .project-card {
    width: 30%;
  }
  .wraper-skills {
    flex-direction: row;
    justify-content: center;
  }
  .wraper-skills img {
    width: 10%;
  }
  .wraper-skills p {
    font-size: 1.5rem;
  }
  form {
    width: 80%;
  }
  .cta a {
    padding: 0.4em 0.5em;
  }
}
@media (min-width: 1080px) {
  .desktop-nav-bar {
    width: 40%;
  }
  form {
    width: 70%;
  }
}
@media (min-width: 1260px) {
  .desktop-nav-bar {
    width: 35%;
  }
}
@media (min-width: 1440px) {
  .desktop-nav-bar {
    width: 30%;
  }
}
